import $ from 'jquery'

$.fn.extend({
  animateCss: function (animationName, callback) {
    let animationEnd = window.WebKitAnimationEvent ? 'webkitAnimationEnd' : (window.AnimationEvent ? 'animationend' : '')
    if (animationEnd) {
      this.addClass('animated ' + animationName).one(animationEnd, function () {
        $(this).removeClass('animated ' + animationName)
        if (callback) {
          callback()
        }
      })
    } else {
      if (callback) {
        callback()
      }
    }
    return this
  }
})
