<?php
$directory = $_GET['directory'];
$handler = opendir($directory);
while(($row = readdir($handler)) !== false)  
{
	$data[] = [
		'name' => $row,
		'url' => $row
	];
}
closedir($handler);

$start = $_GET['start'] ?: 0;
$len = $_GET['len'] ?: 10;

echo json_encode([
	'errno' => 0,
	'errmsg' => 'success',
	'data' => array(
		'total' => count($data),
		'data' => array_slice($data, $start, $len)
	)
]);
